import { StatusBar } from 'expo-status-bar';
import React, { useRef, useEffect } from 'react';
import { Animated, StyleSheet, Text, View, Alert, Button } from 'react-native';

const showAlert = () =>
  Alert.alert("Test Alert", "This is a test alert");

export default function App() {
  const translation = useRef(new Animated.Value(0)).current;

  return (
    <View style={styles.container}>
      <Text>Hello, Welcome To My React Playground</Text>
      <Animated.View style={{width: 100, height: 100, backgroundColor: 'blue', transform: [{ translateX: translation}]}}></Animated.View>
      <Button title='PRESS ME' onPress={Animated.timing(translation, {toValue: 50}).start()}></Button>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
